import { GET_POKEMON, GET_POKEMONS } from "./actionsTypes"



const initialState = {
    allPokemons: [],
    alltypes: []
}

const rootReducer = (state = initialState, action) => {
    switch(action.type){
        case GET_POKEMONS:
            return {
                ...state,
                allPokemons: action.payload
            }
            break;
        return{}
        default:
        return {...state}
    }
}

export default rootReducer
