import { GET_POKEMONS, GET_POKEMON_NAME, GET_TYPES, RESET_FILTERS, GET_POST_BY_ATTACK, GET_POST_BY_ORDER, GET_POST_BY_ORIGIN, GET_POST_BY_TYPE, SELECT_ORDER, SELECT_ORIGIN, SELECT_TYPE, SELECT_ATTACK } from "./actionsTypes"

const initialState = {
    allPokemons: [],
    pokemonsCopy: [],
    allTypes: [],
    filteredPokemons: [],
    selectedType: "",
    selectedAttack: "",
    selectedOrder: "",
    selectedOrigin: ""
}

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_POKEMONS:
      return {
        ...state,
        allPokemons: action.payload,
        pokemonsCopy: action.payload,
      };
    case GET_TYPES:
      return {
        ...state,
        allTypes: action.payload,
      };
    case GET_POKEMON_NAME:
      return {
        ...state,
        allPokemons: action.payload,
        pokemonsCopy: action.payload,
      };

    case SELECT_ORDER:
      return {
        ...state,
        selectedOrder: action.payload,
      };

    case SELECT_TYPE:
      return {
        ...state,
        selectedType: action.payload,
      };

    case SELECT_ORIGIN:
      return {
        ...state,
        selectedOrigin: action.payload,
      };

    case SELECT_ATTACK:
      return {
        ...state,
        selectedAttack: action.payload,
      };
    case GET_POST_BY_ATTACK:
    case GET_POST_BY_ORDER:
    case GET_POST_BY_ORIGIN:
    case GET_POST_BY_TYPE:
      let filteredPosts = state.pokemonsCopy;

      if (state.selectedCategory) {
        filteredPosts = filteredPosts.filter(
          (post) => post.category === state.selectedCategory
        );
      }

      if (state.selectedProvince) {
        filteredPosts = filteredPosts.filter((post) =>
          post.ubication.includes(state.selectedProvince)
        );
      }

      if (state.selectedLocality) {
        filteredPosts = filteredPosts.filter((post) =>
          post.ubication.includes(state.selectedLocality)
        );
      }

    case RESET_FILTERS:
      return {
        ...state,
        selectedOrder: "",
        selectedAttack: "",
        selectedType: "",
        selectedOrigin: "",
      };
    default:
      return { ...state };
  }
            };

export default rootReducer;



