import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const PokemonDetails = () => {
    const { name } = useParams();
    const [pokemon, setPokemon] = useState(null);

    useEffect(() => {
        const fetchPokemonDetails = async () => {
            try {
                // Obtén el ID del Pokémon de alguna manera, por ejemplo, desde la URL.
            const id = pokemon.url.split('/').filter(Boolean).pop();

                // Construye la URL del GIF animado usando el ID del Pokémon.
            const gifUrl = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-v/black-white/animated/${id}.gif`;

                // Crea un objeto de datos del Pokémon con la URL del GIF.
                const pokemonData = {
                  name: name,
                  gifUrl: gifUrl,
                  // Agrega otros detalles como ID, vida, ataque, defensa, etc.
                };

                // Actualiza el estado del componente con los datos del Pokémon.
                setPokemon(pokemonData);
            } catch (error) {
                console.error("Error fetching Pokémon details:", error);
            }
        };
        fetchPokemonDetails();
    }, [name]);

    if (!pokemon) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h2>{pokemon.name}</h2>
            <img src={pokemon.gifUrl} alt={pokemon.name} />
            {/* Mostrar otros detalles como ID, vida, ataque, defensa, etc. */}
        </div>
    );
};

export default PokemonDetails;
