import axios from 'axios';
import './Details.css'
import {useParams } from "react-router-dom"
import React, { useState, useEffect } from "react"

const Detail = () => {
  const [dog, setDog] = useState([])
  const { name } = useParams()

  useEffect(() => {
    const fetchSingleDogData = async () => {
      try {
        const {data} = await axios(`https://api.thedogapi.com/v1/breeds/search?q=${name}`)
        setDog(data)
        console.log(data)
      } catch (error) {
        console.error(error)
      }
    }

    fetchSingleDogData()
  }, [name])

  return (
    <>
      <section className="">
        {dog.map((item) => (
          <div
            key={item.id}
            className=""
          >
            <article>
              <img
                src={`https://cdn2.thedogapi.com/images/${item.reference_image_id}.jpg`}
                alt={item.name}
              />
            </article>
            <article>
              <h1 className="">
                {item.name}
              </h1>
              {item.description && (
                <p className="">
                  {item.description}
                </p>
              )}

              <ul className="">
                <li>
                  <span className="">Bred For: </span>
                  {item.bred_for}
                </li>
                <li>
                  <span className="">Height: </span>
                  {item.height.metric} cm
                </li>
                <li>
                  <span className="">Weight: </span>
                  {item.weight.metric} kgs
                </li>
                <li>
                  <span className="">Breed Group: </span>
                  {item.breed_group}
                </li>
                <li>
                  <span className="">Lifespan: </span>
                  {item.life_span}
                </li>
                <li>
                  <span className="">Temperament: </span>
                  {item.temperament}
                </li>
              </ul>
            </article>
          </div>
        ))}
      </section>
    </>
  )
}
