import React, { useEffect, useState, useRef} from 'react'
import './NavBar.css'
import { Link, useHistory  } from 'react-router-dom'


// const initialBreeds = [
//     {
//         id: 1,
//         name: 'dogo'
//     },
//     {
//         id: 2,
//         name: 'dogo'
//     },
//     {
//         id: 3,
//         name: 'dogo'
//     },
//     {
//         id: 4,
//         name: 'dogo'
//     }
// ];
const Nav = () => {
    const [show, handleShow] = useState(false) 
    // const [breeds, setBreeds] = useState(initialBreeds)
    const history = useHistory()
    const navLogoRef = useRef(null)

    const transitionNavBar = ()=> {
        if(window.scrollY > 100){
            handleShow(true);
        }else{
            handleShow(false);
        }
    };

    useEffect(() => {
        window.addEventListener('scroll', transitionNavBar)
        return () => window.removeEventListener('scroll', transitionNavBar)
    }, [])

    // const handleClick = () => {
    //     window.scrollTo(0, 0) 
    // }
    const handleNavLogoClick = () => {
        if (navLogoRef.current) {
          window.scrollTo(0, 0); // Hace scroll hacia arriba
          history.push('/'); // Redirige a la página de inicio (ajusta la ruta según tu configuración)
        }
      };




    // const onSearch = async (id) => {
    //     try {
    //        const { data } = await axios(`https://api.thedogapi.com/v1/breeds`);

    //        if(data.name) {
    //           setCharacters((oldChars) => [...oldChars, data]);
    //        };

    //     } catch (error) {
    //        alert('¡No hay personajes con este ID!');
    //     }
    //  };

    return (
        <div className={`nav ${show && 'nav_black'}`}>
            <div className="nav_content">
            {/* <img
            onClick={handleClick} 
            className='nav_logo'
            src="https://img2.freepng.es/20180425/fdq/kisspng-dog-puppy-desktop-wallpaper-clip-art-5ae11cfd66d860.3460215715247024614213.jpg" alt="Perrito inicio" /> */}
            <img
            ref={navLogoRef}
            onClick={handleNavLogoClick}
            // onClick={handleClick} 
            className='nav_logo'
            src="https://img.lovepik.com/element/45007/8040.png_860.png" alt="Perrito inicio" />
            </div>
            <div>
            <form className='search_form'>
                <input className='input_search' type="text" name="search" placeholder='Search by breer or ID' />
                {/* <select className="inputs_select">
                    {breeds.map(breed => (
                        <>
                        <option value={breed.id} key={breed.id}>{breed.name}</option>
                    </>
                    ))}
                </select> */}
            </form>
            </div>
            <div className='nav_links'>
            <ul>
                <li>
                    <Link to={'/'}> Inicio</Link>
                </li>
                <li>
                    <Link>Create Dog</Link>
                </li>
                <li>
                    <Link>Favorites</Link>
                </li>
            </ul>
            </div> 
    </div>
    );
}

export default Nav
