import axios from 'axios';
import './DetailPage.css';
import { useParams, Link } from 'react-router-dom';
import React, { useState, useEffect } from 'react';

const Detail = () => {
  const [pokemon, setPokemon] = useState(null);
  const { name } = useParams();

  useEffect(() => {
    const fetchPokemonFromDatabase = async () => {
      try {
        // Hacer una solicitud GET a tu base de datos o servidor local para obtener los detalles del Pokémon
        const { data } = await axios.get(`http://localhost:3001/pokemons/`);
        setPokemon(data.results);
      } catch (error) {
        console.error('Error fetching from database:', error);
        // Si no se encontró en la base de datos, intentar cargar desde la API PokeAPI
        fetchPokemonFromApi();
      }
    };

    const fetchPokemonFromApi = async () => {
      try {
        // Hacer una solicitud GET a la API PokeAPI para obtener los detalles del Pokémon
        const { data } = await axios.get(`https://pokeapi.co/api/v2/pokemon/${name}`);
        setPokemon(data);
      } catch (error) {
        console.error('Error fetching from API:', error);
      }
    };

    // Intentar cargar desde la base de datos primero
    fetchPokemonFromDatabase();
  }, [name]);

  if (!pokemon) {
    return (
      <div className="loading">
        <img
          src="https://images.hive.blog/0x0/https://h93805.files.wordpress.com/2017/01/flaming_charizard.gif"
          alt=""
        />
        Loading...
      </div>
    );
  }

  const gifUrl = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-v/black-white/animated/${pokemon.id}.gif`;

  return (
    <div className="details">
      <div className="detail_card" key={pokemon.id}>
        <h3>#{pokemon.id}</h3>
        <img className="detail_img" src={gifUrl} alt={pokemon.name} />
        <h2>{pokemon.name}</h2>
        <div className="other_details">
          <p>Hp: {pokemon.stats[0].base_stat}</p>
          <p>Height: {pokemon.height} cm</p>
          <p>Weight: {pokemon.weight} kg</p>
          <p>Attack: {pokemon.stats[4].base_stat}</p>
          <p>Defense: {pokemon.stats[2].base_stat}</p>
          <p>Speed: {pokemon.stats[5].base_stat}</p>
          <p>
            Types:{' '}
            {pokemon.types.map((type, index) => (
              <span key={index}>{type.type.name}</span>
            ))}
          </p>

          <Link to="/homePage">
            <button className="button_detail">{'Return'}</button>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Detail;
