import NavBar from '../NavBar/NavBar';
import { Link } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './HomePage.css';

const URL = "https://pokeapi.co/api/v2/pokemon?limit=200";

const HomePage = () => {
  const [pokemons, setPokemons] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [pokemonsPerPage] = useState(12);
  // Mueve la declaración de currentPokemons aquí
  const [currentPokemons, setCurrentPokemons] = useState([]);

  useEffect(() => {
    const fetchPokemons = async () => {
      try {
        const { data } = await axios.get(URL);
        setPokemons(data.results);
        // Asigna los valores a currentPokemons cuando obtengas los datos
        setCurrentPokemons(data.results);
      } catch (error) {
        console.error("Error fetching pokemons:", error);
      }
    };
    fetchPokemons();
  }, []);

  const getPokemonImageURL = (pokemon) => {
    const pokemonId = pokemon.url.split('/').filter(Boolean).pop();
    return `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${pokemonId}.png`;
  };

  const loadPokemonDetails = async (pokemon) => {
    try {
      const { data } = await axios.get(pokemon.url);
      console.log("Types:", data.types.map((type) => type.type.name).join(", "));
    } catch (error) {
      console.error("Error fetching pokemon details:", error);
    }
  };

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div>
      <NavBar />
      <div className="home_general">
        <div className="title_landing"></div>
        <div className="home_cards">
          {currentPokemons.map((pokemon) => (
            <div className="article" key={pokemon.name}>
              <Link to={`/${pokemon.name}`} className="detail_links">
                <img
                  className="pokemon_image"
                  src={getPokemonImageURL(pokemon)}
                  alt={pokemon.name}
                />
                <h2>{pokemon.name}</h2>
                {/* <p onMouseOver={() => loadPokemonDetails(pokemon)}>Ver Tipos</p> */}
              </Link>
            </div>
          ))}
          <div className="pagination-container">
            {Array.from({
              length: Math.ceil(pokemons.length / pokemonsPerPage),
            }).map((_, index) => (
              <button
                key={index}
                onClick={() => paginate(index + 1)}
                className={`pagination-button ${
                  index + 1 === currentPage ? "active" : ""
                }`}
              >
                {index + 1}
              </button>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
