import React, { useState, useEffect } from 'react';
import axios from 'axios';

const URL = "https://pokeapi.co/api/v2/pokemon?limit=200";

const HomePage = () => {
    const [pokemons, setPokemons] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [pokemonsPerPage] = useState(12);

    useEffect(() => {
        const fetchPokemons = async () => {
            try {
                const { data } = await axios.get(`${URL}`);
                setPokemons(data.results);
            } catch (error) {
                console.error("Error fetching pokemons:", error);
            }
        };
        fetchPokemons();
    }, []);

    const paginate = (pageNumber) => {
        setCurrentPage(pageNumber);
    };

    return (
        <div className="home_general">
            <div className='title_landing'>
                <h1>PI Pokemons Henry</h1>
            </div>
            <div className="home_cards">
                {currentPokemons.map((pokemon) => (
                    <div className="article" key={pokemon.name}>
                        <a href={`/${pokemon.name}`} className='detail_links' key={pokemon.name}>
                            <img
                                className='pokemon_image'
                                src={pokemon.url} // Utiliza la URL de imagen proporcionada por la API
                                alt={pokemon.name}
                            />
                            <h2>{pokemon.name}</h2>
                        </a>
                    </div>
                ))}
                <div className="pagination">
                    {Array.from({ length: Math.ceil(pokemons.length / pokemonsPerPage) }).map(
                        (_, index) => (
                            <button
                                key={index}
                                onClick={() => paginate(index + 1)}
                                className={`pagination-button ${
                                    index + 1 === currentPage ? 'active' : ''
                                }`}
                            >
                                {index + 1}
                            </button>
                        )
                    )}
                </div>
            </div>
        </div>
    );
};

export default HomePage;
