import React from "react";
import { useParams, Link } from "react-router-dom";
import './DetailPage.css';
import { useSelector } from 'react-redux';

const Detail = () => {
  const { name } = useParams();
  const allPokemons = useSelector(state => state.allPokemons);

  // Busca el Pokémon por su nombre en allPokemons
  const pokemon = allPokemons.find(p => p.name === name);
  console.log(pokemon);

  if (!pokemon) {
    return (
      <div className='loading'>
        <img
          src="https://images.hive.blog/0x0/https://h93805.files.wordpress.com/2017/01/flaming_charizard.gif"
          alt=""
        />
        Loading...
      </div>
    );
  }

  // Construir la URL del GIF animado usando el ID del Pokémon
  const gifUrl = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-v/black-white/animated/${pokemon.id}.gif`;

  return (
    <div className="details">
      <div className="detail_card" key={pokemon.id}>
        <h3>#{pokemon.id}</h3>
        <img
          className='detail_img'
          src={gifUrl}
          alt={pokemon.name}
        />
        <h2>{pokemon.name}</h2>
        <div className="other_details">
          <p>Hp: {pokemon.stats[0].base_stat}</p>
          <p>Height: {pokemon?.height} cm</p>
          <p>Weight: {pokemon?.weight} kg</p>
          <p>Attack: {pokemon.stats[4].base_stat} </p>
          <p>Defense: {pokemon.stats[2].base_stat}</p>
          <p>Speed: {pokemon.stats[5].base_stat}</p>
          <p>
            Types: {pokemon.types.map((type, index) => (
              <span key={index}>{type.type.name}</span>
            ))}
          </p>

          <Link to='/homePage'>
            <button className='button_detail'>
              {'Return'}
            </button>
          </Link>
        </div>
      </div>
    </div>
  );
}

export default Detail;
