import NavBar from '../NavBar/NavBar';
import { Link } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './HomePage.css';
// import Footer from '../Footer/Footer';

const URL = "https://pokeapi.co/api/v2/pokemon?limit=200";

const HomePage = () => {
  const [pokemons, setPokemons] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [pokemonsPerPage] = useState(12);
  const [pokemonDetails, setPokemonDetails] = useState({});

  useEffect(() => {
    const fetchPokemons = async () => {
      try {
        const { data } = await axios.get(`${URL}`);
        setPokemons(data.results);

        const detailsPromises = data.results.map(async (pokemon) => {
          const { data: details } = await axios.get(pokemon.url);
          return { [pokemon.name]: details };
        });

        const pokemonDetailsData = await Promise.all(detailsPromises);

        const detailsObject = Object.assign({}, ...pokemonDetailsData);
        setPokemonDetails(detailsObject);
      } catch (error) {
        console.error("Error fetching pokemons:", error);
      }
    };
    fetchPokemons();
  }, []);

  const getPokemonImageURL = (pokemon) => {
    const pokemonId = pokemon.url.split('/').filter(Boolean).pop();
    return `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${pokemonId}.png`;
  };

  const getColorForType = (type) => {
    switch (type) {
      case 'grass':
        return '#9bcc50';
      case 'poison':
        return '#b97fc9';
      case 'bug':
        return '#729f3f';
      case 'fire': 
        return '#fd7d24'
      case 'ground':
       return '#a74806'
      case 'electric':
       return '#eed535'
      case'water':
       return '#4592c4'
      case'flying':
       return '#8cb3cd'
      case'fairy':
       return '#ffaec8'
      case'fighting':
       return '#ff3a79'
      case'rock':
       return '#a38c21'
      case'steel':
       return '#9eb7b8'
      case 'psychic':
       return '#cd6284'
      case 'dark':
       return '#262525'
      case 'dragon':
       return '#b00000'
      case 'ice':
       return '#a8c5c3'
      case 'shadow':
       return '#656570'
      case 'ghost':
       return '#7f7f7f'
      case 'normal':
       return '#a4acaf'
      default:
        return 'white';
    }
  };

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const indexOfLastPokemon = currentPage * pokemonsPerPage;
  const indexOfFirstPokemon = indexOfLastPokemon - pokemonsPerPage;
  const currentPokemons = pokemons.slice(indexOfFirstPokemon, indexOfLastPokemon);

  return (
    <div>
      <NavBar />
      <div className="home_general">
        <div className="title_landing"></div>
        <div className="home_cards">
          {currentPokemons.map((pokemon) => (
            <div className="article" key={pokemon.name}>
              <Link to={`/${pokemon.name}`} className="detail_links">
                <img
                  className="pokemon_image"
                  src={getPokemonImageURL(pokemon)}
                  alt={pokemon.name}
                />
                <h2>{pokemon.name}</h2>
                {pokemonDetails[pokemon.name] && (
                  <p>Types: {pokemonDetails[pokemon.name].types.map((type) => (
                    <span key={type.type.name} style={{ backgroundColor: getColorForType(type.type.name) }}>
                      {type.type.name} |>
                    </span>
                  ))}</p>
                )}
              </Link>
            </div>
          ))}
          <div className="pagination-container">
            {Array.from({
              length: Math.ceil(pokemons.length / pokemonsPerPage),
            }).map((_, index) => (
              <button
                key={index}
                onClick={() => paginate(index + 1)}
                className={`pagination-button ${
                  index + 1 === currentPage ? "active" : ""
                }`}
              >
                {index + 1}
              </button>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
