import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const URL = "https://pokeapi.co/api/v2/pokemon?limit=200";

const HomePage = () => {
  const [pokemons, setPokemons] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [pokemonsPerPage] = useState(12);

  useEffect(() => {
    const fetchPokemons = async () => {
      try {
        const { data } = await axios.get(URL);
        setPokemons(data.results);
      } catch (error) {
        console.error("Error fetching pokemons:", error);
      }
    };
    fetchPokemons();
  }, []);

  const getPokemonDetails = async (url) => {
    try {
      const { data } = await axios.get(url);
      return data;
    } catch (error) {
      console.error("Error fetching pokemon details:", error);
    }
  };

  const getPokemonImageURL = (pokemon) => {
    const pokemonId = pokemon.url.split('/').filter(Boolean).pop();
    return `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${pokemonId}.png`;
  };

  const loadPokemonDetails = async (pokemon) => {
    const details = await getPokemonDetails(pokemon.url);
    // Aquí puedes acceder a details.types para obtener los tipos y mostrarlos
    console.log("Types:", details.types);
  };

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div>
      <h1>Pokémon List</h1>
      <ul>
        {pokemons.map((pokemon) => (
          <li key={pokemon.name}>
            <Link
              to={`/${pokemon.name}`}
              className="detail_links"
              key={pokemon.name}
              onMouseOver={() => loadPokemonDetails(pokemon)}
            >
              <img
                className="pokemon_image"
                src={getPokemonImageURL(pokemon)}
                alt={pokemon.name}
              />
              <h2>{pokemon.name}</h2>
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default HomePage;
