import './App.css';
import { Routes, Route} from "react-router-dom"
import LandingPage from '../src/Components/LandingPage/LandingPage';
import HomePage from './Components/HomePage/HomePage';
import Details from './Components/Detail/Details.jsx'
import NavBar from './Components/NavBar/NavBar'
import axios from 'axios';
import { useState } from 'react';
import SearchBar from './Components/SearchBar';


function App() {
  const [doggies, setDoggies] = useState([])


  const onSearch = async (name) => {
    try {
       const { data } = await axios(`https://api.thedogapi.com/v1/breeds/${name}`);

       if(data.name) {
          setDoggies((oldDoggies) => [...oldDoggies, data]);
       };

    } catch (error) {
       alert('¡No hay personajes con este Nombre!');
    }
 };



  return (
    <div className='app'>
      <Routes>
      <Route path='/' element={<LandingPage />} />
      <Route path='/' element={<NavBar />} />
      <Route path='/' element={<SearchBar onSearch={onSearch} />} />
      <Route path='/homePage' element={<HomePage doggies={doggies} />}/>
      {/* <Route path='/homePage' element={<Footer/>}/> */}
      <Route path='/:name' element={<Details/>}/>

      </Routes>

    </div>
  );
}

export default App;
