import { GET_POKEMONS, GET_POKEMON_NAME, GET_TYPES } from "./actionsTypes"

const initialState = {
    allPokemons: [],
    pokemonsCopy: [],
    alltypes: []
}

const rootReducer = (state = initialState, action) => {
    switch(action.type){
        case GET_POKEMONS:
            return {
                // return copy state so we dont "pisar" the state
                ...state,
                allPokemons: action.payload,
                pokemonsCopy: action.payload
            }
            case GET_TYPES:
                return {
                    ...state,
                    types: action.payload
            }
            case GET_POKEMON_NAME:
                return {
                    // return copy state so we dont "pisar" the state
                    ...state,
                    allPokemons: action.payload,
                    pokemonsCopy: action.payload
                }

                    default:
                        return {...state}
                    }
                }

export default rootReducer
