import React, { useEffect, useState } from 'react';
import './NavBar.css';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Nav = () => {
  const [show, handleShow] = useState(false);

  const transitionNavBar = () => {
    if (window.scrollY > 100) {
      handleShow(true);
    } else {
      handleShow(false);
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', transitionNavBar);
    return () => window.removeEventListener('scroll', transitionNavBar);
  }, []);

  const handleClick = () => {
    window.scrollTo(0, 0);
  };

  // Funci贸n para buscar una raza por ID en la API
  const searchBreedById = async (id) => {
    try {
      const response = await axios.get(`https://api.thedogapi.com/v1/breeds/${id}`);
      return response.data;
    } catch (error) {
      console.error('Error al buscar la raza por ID:', error);
      return null;
    }
  };

  // Ejemplo de uso: buscar la raza con ID 1
  useEffect(() => {
    const breedIdToFind = id; // Cambia esto al ID que desees buscar
    searchBreedById(breedIdToFind)
      .then((breed) => {
        if (breed) {
          console.log('Raza encontrada:', breed.name);
        } else {
          console.log(`No se encontr贸 una raza con ID ${breedIdToFind}`);
        }
      });
  }, []);

  return (
    <div className={`nav ${show && 'nav_black'}`}>
      <div className="nav_content">
        <img
          onClick={handleClick}
          className="nav_logo"
          src="https://img.lovepik.com/element/45007/8040.png_860.png"
          alt="Perrito inicio"
        />
      </div>
      <div>
        <form className="search_form">
          <input className="input_search" type="text" name="search" placeholder="Search by breed or ID" />
        </form>
      </div>
      <div className="nav_links">
        <ul>
          <li>
            <Link to="/"> Inicio</Link>
          </li>
          <li>
            <Link>Create Dog</Link>
          </li>
          <li>
            <Link>Favorites</Link>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Nav;
