import React from 'react';
import Card from '../Card/Card';
// import './Cards.css';
import styles from './Cards.module.css';

function Cards({allPokemons}) {

  const pokemons = allPokemons
  const filterPokemonsByOrigin = (pokemons, selectedOrigin) => {
    return pokemons.filter((pokemon) => {
      if (selectedOrigin === 'created') {
        return pokemon.createdInDb;
      } else if (selectedOrigin === 'api') {
        return !pokemon.createdInDb;
      } else {
        return true;
      }
    });
  };

  const filteredPokemons = filterPokemonsByOrigin(allPokemons, selectedOrigin);

  return (
    <div className={styles.all_cards}>
      {filteredPokemons.map((pokemon) => (
        <Card key={pokemon.id} pokemon={pokemon} />
      ))}
    </div>
  );
}

export default Cards
