import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './Details.css'

const Detail = () => {
  const { id } = useParams();
  const [breed, setBreed] = useState(null);

  useEffect(() => {
    // Aquí obtén los detalles de la raza con el ID seleccionado.
    // Puedes hacer una solicitud a tu backend Django para obtener los datos.
    axios.get(`/api/razas/${id}`) // Ajusta la URL según tu backend
      .then(response => {
        const primerPerro = response.data[0];
        const nombre = primerPerro.name;
        const peso = primerPerro.weight.metric;
        const altura = primerPerro.height.metric;

        // Actualiza el estado con los detalles de la raza
        setRaza({
          nombre,
          peso,
          altura,
          // Agrega más detalles aquí según tu estructura de datos
        });
      })
      .catch(error => {
        console.error('Error al obtener los detalles de la raza', error);
      });
  }, [id]);



  if (!breed) {
    return <div>Loading...</div>;
  }

    return(
        <div className="details">
            <div className="detail_card">
                <h3>#{breed?.id}</h3>
                <h2>Breed: {breed?.name}</h2>
                <div className="other_details">
                <p>Height: {breed?.height}</p>
                <p>Weight: {breed?.weight}</p>
                <p>Temperaments: {breed?.temperaments}</p>
                <p>Life Span: {breed.life_span}</p>
                </div>
               </div>
           </div>
)
}
