import {
    GET_POKEMONS,
    GET_TYPES,
    ORDER_BY_NAME,
    FILTER_BY_ORIGIN,
    FILTER_BY_TYPE,
    FILTER_BY_ATTACK,
    RESET_FILTERS,
    SELECT_ATTACK,
    SELECT_ORDER,
    SELECT_ORIGIN,
    SELECT_TYPE
  } from "./actionsTypes";

  const initialState = {
    allPokemons: [],
    pokemonsCopy: [],
    allTypes: [],
    filteredPokemons: [],
    filters: {
      type: "",
      order: "",
      attack: "",
      origin: ""
    }
  };

  const applyFilters = (pokemons, filters) => {
    let filteredPokemons = [...pokemons];

    if (filters.type !== "All") {
      filteredPokemons = filteredPokemons.filter((pokemon) =>
        pokemon.types.includes(filters.type)
      );
    }

    if (filters.origin === "created") {
      filteredPokemons = filteredPokemons.filter((pokemon) => pokemon.createdInDb);
    } else if (filters.origin === "api") {
      filteredPokemons = filteredPokemons.filter(
        (pokemon) => !pokemon.createdInDb
      );
    }

    if (filters.order === "descending") {
      filteredPokemons.sort((a, b) => b.attack - a.attack);
    } else {
      filteredPokemons.sort((a, b) => a.attack - b.attack);
    }

    return filteredPokemons;
  };

  const rootReducer = (state = initialState, action) => {
    switch (action.type) {
      case GET_POKEMONS:
        return {
          ...state,
          allPokemons: action.payload,
          pokemonsCopy: action.payload
        };
      case GET_TYPES:
        return {
          ...state,
          allTypes: action.payload
        };
      case ORDER_BY_NAME:
        const sortedPokemons = state.pokemonsCopy
          .slice()
          .sort((a, b) => (action.payload === "AA" ? a.name.localeCompare(b.name) : b.name.localeCompare(a.name)));

        return {
          ...state,
          pokemonsCopy: sortedPokemons
        };
      case FILTER_BY_TYPE:
      case FILTER_BY_ORIGIN:
      case FILTER_BY_ATTACK:
        return {
          ...state,
          filters: {
            ...state.filters,
            [action.payload.filterType]: action.payload.value
          }
        };
      case RESET_FILTERS:
        return {
          ...state,
          filters: {
            type: "",
            order: "",
            attack: "",
            origin: ""
          }
        };
      case SELECT_ORDER:
      case SELECT_ORIGIN:
      case SELECT_ATTACK:
      case SELECT_TYPE:
        return {
          ...state,
          filters: {
            ...state.filters,
            [action.payload.filterType]: action.payload.value
          }
        };
      default:
        const filteredPokemons = applyFilters(
          state.allPokemons,
          state.filters
        );
        return {
          ...state,
          filteredPokemons
        };
    }
  };

  export default rootReducer;
