import axios from "axios";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import './Details.css'

const Detail = () => {
    const {id} = useParams();
    const [doggie, setDoggie] = useState({});



    useEffect(() => {
        axios(`https://api.thedogapi.com/v1/breeds/${id}`)
        .then(response => response.data)
        .then(( data ) => {
           if (data.name) {
              setDoggie(data);
           } else {  
              alert('No hay personajes con ese ID');
           }
        });
        return setDoggie({});
     }, [id]);


    return(
        <>
        {
            doggie.map((dog) => (
                <div className="article" key={dog.id}>
                <img
                    className='dogs_image'
                    src={'https://cdn2.thedogapi.com/images/BJa4kxc4X.jpg'}
                    alt={dog.name}
                />
                <p>#{dog.id}</p>
                <p>height: {dog.height}</p>
                <h2>Breed: {dog.name}</h2>
                <p>Temperaments: {dog.temperament}</p>
            </div>
        ))}
        </>

    )
}

export default Detail;
