import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './Details.css'

const Detail = () => {
  const { id } = useParams();
  const [breed, setBreed] = useState(null);

  useEffect(() => {
    const fetchBreedDetails = async () => {
      try {
        const response = await axios.get(`https://api.thedogapi.com/v1/breeds/`);
        const primerPerro = response.data[0];
        const nombre = primerPerro.name;
        const peso = primerPerro.weight.metric;
        const altura = primerPerro.height.metric;

// Imprime los valores en la consola
        console.log("Nombre: " + nombre);
        console.log("Peso: " + peso);
        console.log("Altura: " + altura);
        // const breedNames = response.map((breed) => breed.name);
        // console.log(breedNames);


        setBreed(response.data);

    } catch (error) {
        console.error('Error fetching breed details:', error);
    }
};

fetchBreedDetails();
}, [id]);


  if (!breed) {
    return <div>Loading...</div>;
  }

    return(
        <div className="details">
            <div className="detail_card">
                <h3>#{breed?.id}</h3>
                <h2>Breed: {primerPerro.name}</h2>
                <div className="other_details">
                <p>Height: {breed?.height}</p>
                <p>Weight: {breed?.weight}</p>
                <p>Temperaments: {breed?.temperaments}</p>
                <p>Life Long: {breed.life_span}</p>
                </div>
               </div>
           </div>
)
}
export default Detail;

