import React from 'react';
import './Card.css';
import { Link } from 'react-router-dom';
// import Cards from '../Cards/Cards';
import "./Card.css"
import getColorForType from '../../utils/colors';


function Card({pokemon}) {
  const {name, id,image, types} = pokemon

  return (

    <div className="container">
      <Link className='link_uno' to={`/detail/${id}`}>
          {/* <div className='link_img'> */}
              <img src={image} alt={name}  />    
          {/* </div> */}
        </Link>
      <div className='info'>
      <Link className='link'>
      {/* <div className="name_title"> */}
        <span className="">{name}</span>
        {/* </div> */}
          {/* <div className="card_types"> */}
          {types.map(type => <p style={{backgroundColor: getColorForType(type.name)}}>{type} </p>)}
            {/* <span className="">{types.join(' | ')}</span> */}
          {/* </div> */}
      </Link>  
          </div>          
    </div>
  )
}

export default Card
