    event.preventDefault()
    setSearchPokemon(event.target.value)
  }

  function handleSubmit(event){
    event.preventDefault()
  const filtered = allPokemons.filter((user) => user.name.includes(searchPokemon))
  setFiltered(filtered)
  }

