import { GET_POKEMONS } from "./actionsTypes"

const initialState = {
    allPokemons: [],
    alltypes: []
}

const rootReducer = (state = initialState, action) => {
    switch(action.type){
        case "GET_POKEMONS":
            return {
                ...state,
                allPokemons: action.payload
            }
            case "GET_TYPES":
            return {...state, types: action.payload}
        default:
        return {...state}
    }
}

export default rootReducer
