import React, { useState, useEffect } from 'react';
import axios from 'axios';

function PokemonDetail({ match }) {
  const [pokemonData, setPokemonData] = useState(null);

  useEffect(() => {
    // Obtiene el nombre del Pokémon de los parámetros de la URL
    const pokemonName = match.params.name;

    // Hace una solicitud para obtener la información del Pokémon específico
    axios.get(`https://pokeapi.co/api/v2/pokemon/${pokemonName}`)
      .then((response) => {
        setPokemonData(response.data);
      })
      .catch((error) => {
        console.error('Error fetching Pokémon data:', error);
      });
  }, [match.params.name]);

  return (
    <div>
      {pokemonData ? (
        <div>
          <h2>{pokemonData.name}</h2>
          <h3>Types:</h3>
          <ul>
            {pokemonData.types.map((typeData) => (
              <li key={typeData.type.name}>{typeData.type.name}</li>
            ))}
          </ul>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default PokemonDetail;
