import axios from "axios";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import './Details.css'

const Detail = () => {
    const {id} = useParams();
    const [doggie, setDoggie] = useState({});



    useEffect(() => {
        axios(`https://api.thedogapi.com/v1/breeds/${id}`)
        .then(response => response.data)
        .then(( data ) => {
           if (data.id) {
              setDoggie(id);
           } else {  
              alert('No hay personajes con ese ID');
           }
        });
        return setDoggie({});
     }, [id]);


    return(
        <>
        {/* {
            doggie.map((dog) => (
                <div className="article" key={dog.id}>
                <img
                    className='dogs_image'
                    src={'https://cdn2.thedogapi.com/images/BJa4kxc4X.jpg'}
                    alt={dog.name}
                />
                <p>#{dog.id}</p>
                <p>height: {dog.height}</p>
                <h2>Breed: {dog.name}</h2>
                <p>Temperaments: {dog.temperament}</p>
            </div>
        ))} */}
        <div className="detail_card">
         {/* <img src={dog?.image} alt="img" /> */}
         <p>#{doggie?.id}</p>
         <h2>Breed: {doggie?.name}</h2>
         <p>Height: {doggie?.height}</p>
         <p>Weight: {doggie?.weight}</p>
         <p>Temperaments: {doggie?.temperaments}</p>
         <p>Life Long: {doggie.life_long}</p>
        </div>
        </>

    )
}

export default Detail;
