import NavBar from '../../Components/NavBar/NavBar';
import React, {useEffect, useState } from 'react';
import styles from './HomePage.module.css';
import { useDispatch, useSelector } from 'react-redux';
import { filterByAttack, filterByOrigin, filterByType, getPokemon, getPokemonByName, getTypes, orderByName } from '../../Redux/actions';
import Cards from '../../Components/Cards/Cards';
// import getColorForType from '../../utils/colors'
// import Footer from '../Footer/Footer';

const HomePage = () => {
  const dispatch = useDispatch()
  // global state = component subscribed to global state
  const allPokemons = useSelector(state => state.allPokemons)
  // const pokemonsCopy = useSelector((state) => state.pokemonsCopy); //  copia lista de pokemon ordenada

  const allTypes = useSelector(state => state.allTypes)

  const [searchPokemon, setSearchPokemon] = useState("")

  useEffect(() => {
    // when page render will send the action and modify the state
    dispatch(getTypes());
    dispatch(getPokemon())
  },[dispatch])

  function handleClick (event){
    event.preventDefault()
    dispatch(getPokemon())
  }

  function handleChange(event) {
    event.preventDefault()
    setSearchPokemon(event.target.value)
  }

  function handleSubmit(event){
    event.preventDefault();
    if (searchPokemon) {
      dispatch(getPokemonByName(searchPokemon));
    } else {
      dispatch(getPokemon());
    }
  }
  function handleClearSearch() {
    setSearchPokemon(""); // establece el valor de busqueda en una cadena vacia
    dispatch(getPokemon(allPokemons)); // muestra todos los pokemon
  }


  // funcion para manejar el cambio de ORDEN A-Z Z-A
//   const handleOrderChange = (event) => {
//     event.preventDefault();
//     const {value} = event.target; // 'AA' para ascendente, 'ZA' para descendente
//     dispatch(orderByName(value)); // llama a la accion para ordenar los pokemons
//   };

//   // filtrado por Origen
//   const handleOriginFilter = (event)=>{
//     event.preventDefault()
//     const {value} = event.target;
//     dispatch(filterByOrigin(value))
// }

//   // Filtrado por TYPE
//   const handleTypeChange = (event) => {
//     event.preventDefault()
//     const {value} = event.target;
//     dispatch(filterByType(value)); // llama a la accion para filtrar los pokemon por tipo
//   };


//   //Filtrado por Ataque
//   const handlerFilterAtack = (event) => {
//     event.preventDefault()
//     const { value } = event.target;
//     if (value === "All") {
//       dispatch(getPokemon());
//     } else {
//       dispatch(filterByAttack(value));
//     }
//   }
const handleOrderChange = (event) => {
  const { value } = event.target;
  setFilters({ ...filters, order: value });
  dispatch(orderByName(value));
};

const handleTypeChange = (event) => {
  const { value } = event.target;
  setFilters({ ...filters, type: value });
  dispatch(filterByType(value));
};

const handleOriginFilter = (event) => {
  const { value } = event.target;
  setFilters({ ...filters, origin: value });
  dispatch(filterByOrigin(value));
};

const handlerFilterAtack = (event) => {
  const { value } = event.target;
  setFilters({ ...filters, attack: value });
  dispatch(filterByAttack(value === 'All' ? '' : value));
};


  // const handleClearFilters = (event) => {
  //   if(event.target.value === "All"){
  //     dispatch(getPokemon())
  //   }
  // }

  const resetFilters = () => {
    setFilters({ order: 'All', type: 'All', origin: 'All', attack: 'All' });
    dispatch(getPokemon()); // Asegúrate de restablecer la lista completa
  };

  // Paginado
  const [currentPage, setCurrentPage] = useState(1);
  const [pokemonsPerPage] = useState(12);

  const indexOfLastPokemon = currentPage * pokemonsPerPage;
  const indexOfFirstPokemon = indexOfLastPokemon - pokemonsPerPage;
  const currentPokemons = allPokemons.slice(indexOfFirstPokemon, indexOfLastPokemon);

  useEffect(()=>{
    if(currentPokemons.length === 0) setCurrentPage(1);
  },[currentPokemons])


  const nextHandler = () => {
    // verificamos si estamos en la última pagina, para no avanzar mas
    if (indexOfFirstPokemon + pokemonsPerPage >= allPokemons.length) {
      return;
    }
    if (indexOfFirstPokemon >= allPokemons.length) return; // validacion para detenerse
    setCurrentPage(currentPage + 1);
  }

  const prevHandler = () => {
    if (currentPage === 0) return;
    setCurrentPage(currentPage - 1);
  }

  return (
    <>
      <div>
      <NavBar handleChange={handleChange} handleClick={handleClick} handleSubmit={handleSubmit} handleClearSearch={handleClearSearch}/>
      </div>

      <div className={styles.filters}>
      <div>
      // Dentro del render...
<select className={styles.filter_types} onChange={handleOrderChange} name='order' value={filters.order}>
  <option value="All">Order</option>
  <option value="AA">A-Z</option>
  <option value="ZA">Z-A</option>
</select>
<select className={styles.filter_types} onChange={handleTypeChange} name='types' value={filters.type}>
  <option value="All">Types</option>
  {allTypes?.map((tipo) => (
    <option key={tipo} value={tipo}>
      {tipo}
    </option>
  ))}
</select>
<select className={styles.filter_types} name="origin" onChange={handleOriginFilter} value={filters.origin}>
  <option value="All">Created</option>
  <option value="created">DB</option>
  <option value="api">API</option>
</select>
<select className={styles.filter_types} name='attack' onChange={handlerFilterAtack} value={filters.attack}>
  <option value="All">Attack</option>
  <option value="ascending">Ascending (A-Z)</option>
  <option value="descending">Descending (Z-A)</option>
</select>

// ...

<div className={styles.pagination_container}>
  <button className={styles.pagination_button} onClick={prevHandler} disabled={currentPage === 1}>
    Prev
  </button>
  <button className={styles.pagination_button}>{currentPage}</button>
  <button className={styles.pagination_button} onClick={nextHandler} disabled={indexOfFirstPokemon + pokemonsPerPage >= allPokemons.length}>
    Next
  </button>
</div>

    </div>
      </div>

    <div className={styles.home_cards}> 
        <Cards allPokemons={currentPokemons} />
    </div>
    <div className={styles.pagination_container}>
      <button className={styles.pagination_button} onClick={prevHandler}  disabled={currentPage === 1}>Prev</button>
      <button className={styles.pagination_button} >{currentPage}</button>
      <button className={styles.pagination_button} onClick={nextHandler} disabled={indexOfFirstPokemon + pokemonsPerPage >= allPokemons.length}>Next</button>
    </div>
    </>
  );
};

export default HomePage;
