import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './HomePage.css';

const URL = "https://api.thedogapi.com/v1/breeds";

const HomePage = () => {
  const [dogs, setDogs] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [dogsPerPage] = useState(10);

  useEffect(() => {
    const fetchDogs = async () => {
      try {
        const { data } = await axios.get(`${URL}`);
        setDogs(data);
      } catch (error) {
        console.error("Error fetching dogs:", error);
      }
    };
    fetchDogs();
  }, []);

  const truncer = (string, n) => {
    return string?.length > n ? string.substr(0, n - 1) + '...' : string;
  };

  const indexOfLastDog = currentPage * dogsPerPage;
  const indexOfFirstDog = indexOfLastDog - dogsPerPage;
  const currentDogs = dogs.slice(indexOfFirstDog, indexOfLastDog);

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div className="home_general">
      {/* ... Rest of your code ... */}
      <div className="home_cards">
        {currentDogs.map((dog) => (
          <div className="article" key={dog.id}>
            <img
              src={'https://cdn2.thedogapi.com/images/BJa4kxc4X.jpg'}
              alt={dog.name}
            />
            <h2>{dog.name}</h2>
            <p>{truncer(dog?.temperament, 30)}</p>
          </div>
        ))}
      </div>
      <div className="pagination">
        {Array.from({ length: Math.ceil(dogs.length / dogsPerPage) }).map(
          (_, index) => (
            <button
              key={index}
              onClick={() => paginate(index + 1)}
              className={`pagination-button ${
                index + 1 === currentPage ? 'active' : ''
              }`}
            >
              {index + 1}
            </button>
          )
        )}
      </div>
    </div>
  );
};

export default HomePage;
