import axios from 'axios';
import './DetailPage.css'
import { useParams, Link } from "react-router-dom"
import React, { useState, useEffect } from "react"
import { getPokemon } from '../../Redux/actions';
import { useDispatch, useSelector } from 'react-redux';


const Detail = () => {
  const {id} = useParams()

  const dispatch = useDispatch()
  // global state = component subscribed to global state
  const allPokemons = useSelector(state => state.allPokemons)
  // console.log(allPokemons);  
  // const [pokemon, setPokemon] = useState();
  // console.log(pokemon);
  // const { name } = useParams();

  useEffect(() => {
    // when page render will send the action and modify the state
    dispatch(getPokemon())
  },[dispatch])
    // useEffect(() => {
    //     const fetchSinglePokemonData = async () => {
    //         try {
    //             const { data } = await axios(`https://pokeapi.co/api/v2/pokemon/${name}`);
    //             setPokemon(data);
    //         } catch (error) {
    //             return error
    //         }
    //     }

    //     fetchSinglePokemonData();
    // }, [name]);

    if (!allPokemons) {
        return <div className='loading'>
          <img src="https://images.hive.blog/0x0/https://h93805.files.wordpress.com/2017/01/flaming_charizard.gif" 
          alt="" />
          Loading...
        </div>;
    }

    // Construir la URL del GIF animado usando el ID del Pokémon
    const gifUrl = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-v/black-white/animated/${allPokemons.id}.gif`;


  return (
    <div className="details">
      <div className="detail_card" key={allPokemons.id}>
        <h3>#{allPokemons.id}</h3>
        <img
            className='detail_img'
            src={gifUrl} 
            alt={allPokemons.name}
            />
            <h2>{allPokemons.name}</h2>
        <div className="other_details">
          <p>Hp: {allPokemons.stats[0].base_stat}</p>
          <p>Height: {allPokemons?.height} cm</p>
          <p>Weight: {allPokemons?.weight} kg</p>
          <p>Attack: {allPokemons.stats[1].base_stat} </p>
          <p>Defense: {allPokemons.stats[2].base_stat}</p>
          <p>Speed: {allPokemons.stats[5].base_stat}</p>
          {/* <p>Type: {allPokemons?.types.stat.name}</p>   */}
          <p>Types: {allPokemons.types.map((type, index) => (
    <span key={index}>{type.type.name}</span>
  ))}</p>

          <Link to='/homePage'>
            <button className='button_detail'>
              {'Return'}
            </button>
          </Link>
        </div>
      </div>
    </div>
  );
}

export default Detail;
