import axios from 'axios';
import './DetailPage.css'
import {useParams, Link } from "react-router-dom"
import React, { useState, useEffect } from "react"

const Detail = () => {
  const [pokemon, setPokemon] = useState([])
//   Get the name param from the URL
  const { name } = useParams()

  useEffect(() => {
    const fetchSingleDogData = async () => {
      try {
          const {data} = await axios(`https://pokeapi.co/api/v2/pokemon/${name}`)
          setPokemon(data)
        // console.log(data)
      } catch (error) {
        console.error(error)
      }
    }

    fetchSingleDogData()
  }, [name])

//   const getPokemonImageURL = (pokemon) => {
//     // Construir la URL de la imagen usando el ID del Pokémon. Esta function toma un objeto Pokémon de la lista y extrae su ID de la URL para construir la URL de la imagen adecuada. Esto debería mostrar las imágenes de los Pokémon en tu página de inicio.
//     const pokemonId = pokemon.url.split('/').filter(Boolean).pop();
//     return `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${pokemonId}.png`;
// }; 

if (!pokemon) {
    return <div>Loading...</div>;
}

  return (
    <div className="details">
      <div className="detail_card" key={pokemon.id}>
        {/* <h3>#{pokemon?.id}</h3> */}
        <h2>{pokemon?.name}</h2>
        <img 
        // src={`https://cdn2.thedogapi.com/images/${item.reference_image_id}.jpg`}
        src={pokemon?.urlGif}
        alt={pokemon.name}/>
        <div className="other_details">
          <p>Height: {pokemon?.height} cm</p>
          <p>Weight: {pokemon?.weight} kg</p>
          <p>Type: {pokemon?.type}</p>
          <p>Life: {pokemon?.life}</p>
        <Link to='/homePage'>
          <button className='button_detail'>
            {'Return'}
          </button>
        </Link>
        </div>
      </div>
  {/* ))} */}
  </div>
  );
}

export default Detail
