import { GET_POKEMONS, GET_POKEMON_NAME, GET_TYPES, ORDER_BY_NAME, FILTER_BY_ORIGIN, FILTER_BY_TYPE } from "./actionsTypes"

const initialState = {
    allPokemons: [],
    pokemonsCopy: [],
    allTypes: []
}

const rootReducer = (state = initialState, action) => {
    switch(action.type){
        case GET_POKEMONS:
            return {
                // return copy state so we dont "pisar" the state
                ...state,
                allPokemons: action.payload,
                pokemonsCopy: action.payload
            }
            case GET_TYPES:
                return {
                    ...state,
                    allTypes: action.payload
            }
            case GET_POKEMON_NAME:
                return {
                    // return copy state so we dont "pisar" the state
                    ...state,
                    allPokemons: action.payload,
                    pokemonsCopy: action.payload
                }

                case ORDER_BY_NAME:
                    const copy = [...state.pokemonsCopy]; // Copia la matriz de pokemonsCopy
                    let sortedPokemons;

                    if (action.payload === 'AA') {
                      // Orden ascendente
                      sortedPokemons = copy.sort((a, b) => {
                        if (a.name < b.name) return -1;
                        if (a.name > b.name) return 1;
                        return 0;
                      });
                    } else if (action.payload === 'ZA') {
                      // Orden descendente
                      sortedPokemons = copy.sort((a, b) => {
                        if (a.name < b.name) return 1;
                        if (a.name > b.name) return -1;
                        return 0;
                      });
                    } else {
                      // Si la direcci칩n del orden no es v치lida, devuelve el estado actual
                      return state;
                    }
                    return {
                        ...state,
                        allPokemons: sortedPokemons,
                      };

            case FILTER_BY_TYPE:
                let filterType;
                if (action.payload === "All") {
                    filterType = state.pokemonsCopy;
                } else {
                    filterType = state.pokemonsCopy.filter((event) =>
                    event.types.includes(action.payload)
                    );
                }
                return {
                    ...state,
                    allPokemons: filterType,
                }; 

                case FILTER_BY_ORIGIN:
                    const createdFilter = action.payload === "created"
                    ? state.pokemonsCopy.filter((event) => event.createdInDb)
                    : state.pokemonsCopy.filter((event) => !event.createdInDb);
            return {
                ...state,
                allPokemons: action.payload === "All" ? state.pokemonsCopy : createdFilter,
            }
                default:
                    return {...state}
            }
    }

export default rootReducer
