    //         const urlRegex = /^(https?:\/\/)?([a-zA-Z0-9.-]+)(\.[a-zA-Z]{2,})(:\d{1,5})?(\/\S*)?$/;
    //         if (state.image === '') {
    //           return setErrors({ ...errors, image: 'The image URL is required' });
    //         }
    //         if(!urlRegex.test(state.image)){
    //           return setErrors({...errors, image: 'URL inválida'})
    //         } else {
    //           return setErrors({ ...errors, image: '' });
    //         };

    //     }
    //     if(name === "hp"){
    //         if(isNaN(parseInt(state.hp))) setErrors({...errors, hp: "hp must be a number"})
    //         else if(!/^[0-9]{1,3}$/.test(state.hp)) setErrors({...errors, hp: "Life must be between 0-999"})
    //         else setErrors({...errors, hp: ''})
    //     }
    //     if(name === "attack"){
    //         if(isNaN(parseInt(state.attack))) setErrors({...errors, attack: "Attack must be a number"})
    //         else if(!/^[0-9]{1,3}$/.test(state.attack)) setErrors({...errors, attack: "Attack must be between 0-999"})
    //         else setErrors({...errors, attack: ''})
    //     }
    //     if(name === "defense"){
    //         if(isNaN(parseInt(state.defense))) setErrors({...errors, defense: "Defense must be a number"})
    //         else if(!/^[0-9]{1,3}$/.test(state.defense)) setErrors({...errors, defense: "Defense must be between 0-999"})
    //         else setErrors({...errors, defense: ''})
    //     }
    //     if(name === "speed"){
    //         if(isNaN(parseInt(state.speed))) setErrors({...errors, speed: "Speed must be a number"})
    //         else if(!/^[0-9]{1,3}$/.test(state.speed)) setErrors({...errors, speed: "Speed must be between 0-999"})
    //         else setErrors({...errors, speed: ''})
    //     }
    //     if(name === "height"){
    //         if(isNaN(parseInt(state.height))) setErrors({...errors, height: "Height must be a number"})
    //         else if(!/^[0-9]{1,3}$/.test(state.height)) setErrors({...errors, height: "Height must be between 0-999"})
    //         else setErrors({...errors, height : ''})
    //     }
    //     if(name === "weight"){
    //         if(isNaN(parseInt(state.weight))) setErrors({...errors, weight: "Weight must be a number"})
    //         else if(!/^[0-9]{1,3}$/.test(state.weight)) setErrors({...errors, weight: "Weight must be between 0-999"})
    //         else setErrors({...errors, weight : ''})
    //     }
    // }

    const validation = (state, fieldName) => {
        switch (fieldName) {
          case 'name':
            if (state.name === '') {
              return setErrors({ ...errors, name: 'El nombre es requerido' });
            }
            if(state.name.length < 5 || state.name.length > 30) return setErrors({ ...errors, name: 'El nombre requiere entre 5 a 30 carácteres' });
            else {
              return setErrors({ ...errors, name: '' });
            };

          case 'hp':
            if(isNaN(parseInt(state.hp))) return setErrors({...errors, hp: 'Requiere que ser un número'});
            if(state.hp < 0) return setErrors({...errors, hp: 'Se requiere numero positivo'});
            else return setErrors({...errors, hp: ''});
          case 'attack':
            if(isNaN(parseInt(state.attack))) return setErrors({...errors, attack: 'Requiere que ser un número'});
            if(state.attack < 0) return setErrors({...errors, attack: 'Se requiere numero positivo'});
            else return setErrors({...errors, attack: ''});
          case 'defense':
            if(isNaN(parseInt(state.defense))) return setErrors({...errors, defense: 'Requiere que ser un número'});
            if(state.defense < 0) return setErrors({...errors, defense: 'Se requiere numero positivo'});
            else return setErrors({...errors, defense: ''});
          case 'speed':
            if(isNaN(parseInt(state.speed))) return setErrors({...errors, speed: 'Requiere que ser un número'});
            if(state.speed < 0) return setErrors({...errors, speed: 'Se requiere numero positivo'});
            else return setErrors({...errors, speed: ''});
            case 'height':
              if(isNaN(parseInt(state.height))) return setErrors({...errors, height: 'Requiere que ser un número'});
              if(state.height < 0) return setErrors({...errors, height: 'Se requiere numero positivo'});
              else return setErrors({...errors, height: ''});    
          case 'weight':
            if(isNaN(parseInt(state.weight))) return setErrors({...errors, weight: 'Requiere que ser un número'});
            if(state.weight < 0) return setErrors({...errors, weight: 'Se requiere numero positivo'});
            else return setErrors({...errors, weight: ''});

          case 'image':
            const urlRegex = /^(https?:\/\/)?([a-zA-Z0-9.-]+)(\.[a-zA-Z]{2,})(:\d{1,5})?(\/\S*)?$/;
            if (state.image === '') {
              return setErrors({ ...errors, image: 'La URL de la imagen es requerida' });
            }
            if(!urlRegex.test(state.image)){
              return setErrors({...errors, image: 'URL inválida'})
            } else {
              return setErrors({ ...errors, image: '' });
            };
          case 'types':
              if(state.types.length > 1) setErrors({...errors, types: ''});
                break

          default:
            break;
        }
      }



    const handleChange = (event) => {
        event.preventDefault()

        if(event.target.name === "types"){
            if(state.types.length > 1) setErrors({...errors, types: "Select max two types"})
            else if(state.types.includes(event.target.value)) return
            setState({
                ...state,
                [event.target.name]: [...state[event.target.name], event.target.value]
            }) 
            return 
        }

        setState({
            ...state,
            [event.target.name]: event.target.value
        })
        validation({
            ...state,
            [event.target.name]: event.target.value, 
        }, event.target.name)
    }

    // const disableButton = () => {
    //     for (let error in errors) {
    //       if (errors[error] !== "") return true;
    //     }
    // }


    const removeTipo = (event) => {
        // const value = document.getElementById(event.target.name).value
        setState({
            ...state,
            [event.target.name]: [...state[event.target.name].filter(x => x!== event.target.id)]
        })
    }

    const handleSubmit = (event) => {
        event.preventDefault(); 
        dispatch(postPokemon(state))
    }


    return (
        <div className='form_general'>
            {console.log(errors)}
        <form className='pokemon_form' onSubmit={handleSubmit} >
            <input onChange={handleChange} type="text" name='name' placeholder='Name'/>
            <span>{errors.name}</span>
            <input onChange={handleChange} type="text" name='image' placeholder='image'/>
            <input onChange={handleChange} type="number" name='hp' placeholder='hp'/>
            <span>{errors.hp}</span>
            <input onChange={handleChange} type="number" name='attack' placeholder='Attack'/>
            <span>{errors.attack}</span>
            <input onChange={handleChange} type="number" name='defense' placeholder='Defense'/>
            <span>{errors.defense}</span>
            <input onChange={handleChange} type="number" name='speed' placeholder='Speed'/>
            <span>{errors.speed}</span>
            <input onChange={handleChange} type="number" name='height' placeholder='Height'/>
            <span>{errors.height}</span>
            <input onChange={handleChange} type="number" name='weight' placeholder='Weight'/>
            <span>{errors.weight}</span>
                <div className='type_options'>
                <label>Types: </label>
                <select onChange={handleChange} name="types" id="">
                {types?.map(tipo => <option key={tipo} value={tipo}>{tipo}</option>)}   
                </select>
                    {
                        state.types.map((tipo, index) => (
                          <div key={index}>
                            <span id={"types"}>{tipo}</span>
                            <button id={tipo} type='button' name="types" onClick={removeTipo}>X</button>
                          </div>
                        ))
                    }

                <button onClick={handleChange} type='submit'>Submit</button>

