import NavBar from '../../Components/NavBar/NavBar';
import React, {useEffect, useState } from 'react';
import './HomePage.css';
// import getColorForType from '../../utils/colors'
import { useDispatch, useSelector } from 'react-redux';
import { getPokemon, getPokemonByName } from '../../Redux/actions';
import Cards from '../../Components/Cards/Cards';
// import Footer from '../Footer/Footer';

const HomePage = () => {
  const dispatch = useDispatch()
  // global state = component subscribed to global state
  const allPokemons = useSelector(state => state.allPokemons)
  const [searchPokemon, setSearchPokemon] = useState("")


  //estados locales
  // const [filtered, setFiltered] = useState(allPokemons)

  // Filtrar Backend
  function handleChange(event) {
    event.preventDefault()
    setSearchPokemon(event.target.value)
  }
  function handleSubmit(event){
    event.preventDefault()
    dispatch(getPokemonByName(searchPokemon))
  }


  // Filtrar Api


  const [currentPage, setCurrentPage] = useState(1);
  const [pokemonsPerPage] = useState(12);

  useEffect(() => {
    // when page render will send the action and modify the state
    dispatch(getPokemon())
  },[dispatch])

  // useEffect(() => {
  //   const fetchPokemons = async () => {
  //     try {
  //       const { data } = await axios.get(`${URL}`);
  //       setPokemons(data.results);

  //       const detailsPromises = data.results.map(async (pokemon) => {
  //         const { data: details } = await axios.get(pokemon.url);
  //         return { [pokemon.name]: details };
  //       });

  //       const pokemonDetailsData = await Promise.all(detailsPromises);

  //       const detailsObject = Object.assign({}, ...pokemonDetailsData);
  //       setPokemonDetails(detailsObject);
  //     } catch (error) {
  //       console.error("Error fetching pokemons:", error);
  //     }
  //   };
  //   fetchPokemons();
  // }, []);




  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const indexOfLastPokemon = currentPage * pokemonsPerPage;
  const indexOfFirstPokemon = indexOfLastPokemon - pokemonsPerPage;
  const currentPokemons = allPokemons.slice(indexOfFirstPokemon, indexOfLastPokemon);

  useEffect(()=>{
    if(currentPokemons.length === 0) setCurrentPage(1);
  },[currentPokemons])


  const nextHandler = () => {
    if (indexOfFirstPokemon >= allPokemons.length) return; // Validación para detenerse
    setCurrentPage(currentPage + 1);
  }

  const prevHandler = () => {
    if (currentPage === 0) return;
    setCurrentPage(currentPage - 1);
  }


  return (
    <div className='home_general'>
      <div>
      <NavBar handleChange={handleChange} handleSubmit={handleSubmit}/>
      </div>
    <div className='home_cards'> 
        <Cards allPokemons={currentPokemons} />
    </div>
      <button className="" onClick={prevHandler}>Prev</button>
      <button className="" >{currentPage}</button>
      <button className="" onClick={nextHandler}>Next</button>
      {/*
          <div className="pagination-container">
            {Array.from({
              length: Math.ceil(pokemons.length / pokemonsPerPage),
            }).map((_, index) => (
              <button
                key={index}
                onClick={() => paginate(index + 1)}
                className={`pagination-button ${
                  index + 1 === currentPage ? "active" : ""
                }`}
              >
                {index + 1}
              </button>
            ))}
          </div>
        </div>
      </div> */}
    </div>
  );
};

export default HomePage;
