import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './Details.css'

const Detail = () => {
  const { id } = useParams();
  const [breed, setBreed] = useState(null);

  useEffect(() => {
    const fetchBreedDetails = async () => {
      try {
        axios.get(`https://api.thedogapi.com/v1/breeds/`);
        TouchEvent(response => {
            const primerPerro = response.data[0];
            const name = primerPerro.name;
            const weight = primerPerro.weight.metric;
            const height = primerPerro.height.metric;
            const id = primerPerro.id;
            const image = primerPerro.image;
            const temperaments = primerPerro.temperaments;
            const life = primerPerro.life_span

            setBreed({
                id,
                name,
                weight,
                image,
                height,
                temperaments,
                life
            })
        })
    } catch (error) {
        console.error('Error fetching breed details:', error);
    }
};
}, [id]);


  if (!breed) {
    return <div>Loading...</div>;
  }

    return(
        <div className="details">
            <div className="detail_card">
                <h3>#{breed?.id}</h3>
                <h2>Breed: {breed?.name}</h2>
                <div className="other_details">
                <p>Height: {breed?.height}</p>
                <p>Weight: {breed?.weight}</p>
                <p>Temperaments: {breed?.temperaments}</p>
                <p>Life Span: {breed.life_span}</p>
                </div>
               </div>
           </div>
)
}
export default Detail;

