import { GET_POKEMONS, GET_POKEMON_NAME, GET_TYPES, ORDER_BY_NAME } from "./actionsTypes"

const initialState = {
    allPokemons: [],
    pokemonsCopy: [],
    alltypes: []
}

const rootReducer = (state = initialState, action) => {
    switch(action.type){
        case GET_POKEMONS:
            return {
                // return copy state so we dont "pisar" the state
                ...state,
                allPokemons: action.payload,
                pokemonsCopy: action.payload
            }
            case GET_TYPES:
                return {
                    ...state,
                    alltypes: action.payload
            }
            case GET_POKEMON_NAME:
                return {
                    // return copy state so we dont "pisar" the state
                    ...state,
                    allPokemons: action.payload,
                    pokemonsCopy: action.payload
                }

            case ORDER_BY_NAME:
            let pokemonsCopy = state.pokemones;
            if (action.payload === 'AA') {
                return {
                    ...state, pokemonsCopy: pokemonsCopy.sort((a, b) => {
                        if (a.name < b.name) return -1
                        if (a.name > b.name) return 1
                        return 0
                    }).map(p => p)
                }
            } else {
                return {
                    ...state, pokemonsCopy: pokemonsCopy.sort((a, b) => {
                        if (a.name < b.name) return 1
                        if (a.name > b.name) return -1
                        return 0
                    }).map(p => p)
                }
            }
                    default:
                        return {...state}
                    }
                }

                export default rootReducer
