// import NavBar from '../NavBar/NavBar'
// import { Link } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './HomePage.css';
// import Footer from '../Footer/Footer';

const URL = "https://pokeapi.co/api/v2/pokemon?limit=200";

const HomePage = () => {
  const [pokemons, setPokemons] = useState([]);
  // Here I create two states first for the current page, set to 1. Second I set dog per page to 8.
  const [currentPage, setCurrentPage] = useState(1);
  const [pokemonsPerPage] = useState(8);

  useEffect(() => {
    const fetchPokemons = async () => {
      try {
        const { data } = await axios.get(`${URL}`);
        // const id = data.find((dogsId) => dogsId.id)
        // console.log(id);
        setPokemons(data);
      } catch (error) {
        console.error("Error fetching pokemons:", error);
      }
    };
    fetchPokemons();
    //UseEffect runs when component updates or didmount so
    // Empty array for stopping out the effect. If I want to run when some specific change you can use dependencies
  }, []);

  const truncer = (string, n) => {
    return string?.length > n ? string.substr(0, n - 1) + '...' : string;
  };

  // Get current dogs
  const indexOfLastPokemon = currentPage * pokemonsPerPage;
  const indexOfFirstPokemon = indexOfLastPokemon - pokemonsPerPage;
  const currentPokemons = pokemons.slice(indexOfFirstPokemon, indexOfLastPokemon);

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };


  return (
    <div className="home_general">
        <NavBar />

          <div className='title_landing'>
           <h1>PI Dogs Henry</h1>
        </div>

      <div className="home_cards">
        {
            currentPokemons.map((dog) => (
                <div className="article" key={dog.id}>
                <Link to={`/${dog.name}`} className='detail_links' key={dog.id}>        
                <img
                    className='dogs_image'
                    src={`https://cdn2.thedogapi.com/images/${dog.reference_image_id}.jpg`}
                    alt={dog.name}
                />
                <h2>{dog.name}</h2>
                <p>{dog.weight.metric} kg</p>
                <p>{truncer(dog?.temperament, 30)}</p>
                </Link> 
            </div>
        ))}


        <div className="pagination">
        {Array.from({ length: Math.ceil(dogs.length / dogsPerPage) }).map(
        (_, index) => (
        <button
            key={index}
            onClick={() => paginate(index + 1)}
            className={`pagination-button ${
            index + 1 === currentPage ? 'active' : ''
            }`}
        >
            {index + 1}
        </button>
        )
        )}

        </div>
      </div>
      <Footer />
    </div>
  );
};

export default HomePage;
